import React, { useState } from 'react'
import { Card, FormControl, InputLabel, OutlinedInput, FormHelperText, FormGroup, CardContent, Typography, Box, Link, Button, makeStyles } from '@material-ui/core';
import red from '@material-ui/core/colors/red'
import { useLocation } from 'react-router-dom';
import userEvent from '@testing-library/user-event';

const useStyles = makeStyles(theme => ({
    form: { padding: "2rem 1rem" },
    formActions: { display: "flex", justifyContent: "space-between", alignItems: "center" },
    error: { color: red[900] }
}))

const CommonCard = (props) => {
    // const [data, setData] = useState({ email: "", psw: "" });
    const [email, setEmail] = useState("");
    const [psw, setPsw] = useState("")
    const [error, setError] = useState("")
    const classes = useStyles();
    const location = useLocation();

    const userInput = (e) => {
        const { name, value } = e.target;
        console.log(name, value)
        setData(prev => {
            return {
                ...prev,
                [name]: value
            }
        })
    }

    const validateAndSubmit = (e) => {
        e.preventDefault();
        console.log(email)
        if (location.pathname === "/") {
            if (email === "") {
                setError("Email can't be set empty.")
            }
        }
    }

    return (
        <>
            <Card>
                <CardContent>
                    <Box>
                        <Box>
                            <Typography align="center" variant="h4" gutterBottom>Google</Typography>
                            <Typography align="center" variant="h5" gutterBottom>Sign in</Typography>
                            <Typography align="center" variant="subtitle1">to continue to Gmail</Typography>
                        </Box>
                        <Box px={3} py={2}>
                            <form onSubmit={validateAndSubmit}>
                                <FormGroup>
                                    <FormControl fullWidth variant="outlined">
                                        <InputLabel htmlFor="outlined-email">{props.label}</InputLabel>
                                        <OutlinedInput type={props.type} labelWidth={props.labelWidth} name={props.name} value={location.pathname === "/" ? email : psw} onClick={userInput} />
                                    </FormControl>
                                    <FormHelperText className={classes.error}>{error}</FormHelperText>
                                </FormGroup>
                                <Link href="#">{location.pathname === "/" ? "Forgot email" : "Forgot Password"}</Link>
                                <Box className={classes.formActions} my={2} py={1}>
                                    <Link href="#">Create account</Link>
                                    <Button type="submit" variant="contained" color="primary">Next</Button>
                                </Box>
                            </form>
                        </Box>
                    </Box>
                </CardContent>
            </Card>
        </>
    )
}

export default CommonCard
