import React from 'react'
import { Card, CardContent, Typography, Box, Link, Button, makeStyles } from '@material-ui/core';
import { useLocation } from 'react-router-dom';
import { FormEmailInput, FormPasswordInput } from '../Component';

const useStyles = makeStyles(theme => ({
    form: { padding: "2rem 1rem" },
    formActions: { display: "flex", justifyContent: "space-between", alignItems: "center" },
    nextBtn: {}
}))

const CommonCard = () => {
    const classes = useStyles();
    const location = useLocation();
    return (
        <>
            <Card>
                <CardContent>
                    <Box>
                        <Box>
                            <Typography align="center" variant="h4" gutterBottom>Google</Typography>
                            <Typography align="center" variant="h5" gutterBottom>Sign in</Typography>
                            <Typography align="center" variant="subtitle1">to continue to Gmail</Typography>
                        </Box>
                        <Box px={3} py={2}>
                            <form>
                                {location.pathname === "/" ? <FormEmailInput /> : <FormPasswordInput />}
                                <Link href="#">{location.pathname === "/" ? "Forgot email" : "Forgot Password"}</Link>
                                <Box className={classes.formActions} my={2} py={1}>
                                    <Link href="#">Create account</Link>
                                    <Button type="submit" variant="contained" color="primary" component={Link} >Next</Button>
                                </Box>
                            </form>
                        </Box>
                    </Box>
                </CardContent>
            </Card>
        </>
    )
}

export default CommonCard
