const validateEmail = (email) => {
    let atSymbol = email.indexOf('@');
    let dot = email.lastIndexOf('.');
    if (email === "") {
        return false;
    } if (atSymbol === -1) {
        return false
    } if (dot === -1) {
        return false
    }
}

export default validateEmail;
